document.addEventListener("DOMContentLoaded", () => {
  const hamburger = document.getElementById("hamburger");
  const menu = document.getElementById("menu");

  if (hamburger && menu) {
    hamburger.addEventListener("click", () => {
      hamburger.classList.toggle("is-active");
      menu.classList.toggle("open");
      document.body.classList.toggle("overlay-open");
    });
  }

  const fancyboxGroups = {
    "holy-unholy": {},
    "new-york": {},
    "portland": {},
    "specters": {},
    "montauk": {},
    "andy": {},
    "vespers-collection": {},
    "melancholic-collection": {},
    "accessories": {},
    "process": {},
    "digital": { videoAutoplay: true },
    "printmaking": { videoAutoplay: true },
    "drawing": { videoAutoplay: true },
  };

  Object.entries(fancyboxGroups).forEach(([group, extraOptions]) => {
    const selector = `[data-fancybox="${group}"]`;
    if (document.querySelector(selector)) {
      Fancybox.bind(selector, {
        Images: { preload: false },
        Toolbar: { display: ["close"] },
        Carousel: { Navigation: true, infinite: true },
        Thumbs: false,
        caption: null,
        ...extraOptions,
      });
    }
  });
});
